        except ValueError:
            raise EmojiLangException("Expected float input")
    elif prog.READ_STRING():
        return StringValue(input("> "))
    elif prog.printExpr():
        value = self.interp(prog.printExpr().expr(), env)
        if isinstance(value, StringValue):
